mapper.insert:

    SELECT IFNULL(MAX(user_no), 0) + 1 FROM tb_user

INSERT INTO 	tb_user
(
            user_no
    ,		login_id
    ,		user_name
    ,		user_email
    ,		password
    ,		join_date
) VALUES (
            #{user_no}
    ,		#{login_id}
    ,		#{user_name}
    ,		#{user_email}
    ,		#{password}
    ,		NOW()
)

mapper.select:
SELECT      COUNT(*)
FROM        tb_user
WHERE       login_id = #{login_id}
